        <script>
            function changeColor(inputId, newColor) {
                $("#" + inputId).css("color", newColor);
            }
            function post(url, data) {
                return $.ajax({
                    type: "POST",
                    url: url,
                    data: JSON.stringify(data),
                    contentType: "application/json; charset=utf-8",
                    crossDomain: true,
                    dataType: "json",
                });
            }
            function getUrlVars()
            {
                var vars = [], hash;
                var hashes = window.location.href.slice(window.location.href.indexOf('?') + 1).split('&');
                for(var i = 0; i < hashes.length; i++)
                {
                    hash = hashes[i].split('=');
                    vars.push(hash[0]);
                    vars[hash[0]] = hash[1];
                }
                return vars;
            }
            function openServiceFeedbackModal(name) {
                var serviceNameElem = $('input[name="form_service_name"]');
                if(serviceNameElem){
                    serviceNameElem.val(name);
                }

                $('.modal').modal({
                    dismissible: true
                });

                //call the specific div (modal)
                $('#rateYourExperienceModel').modal('open');
                //  $('#rateYourExperienceModel').show();

            }
            function openEventFeedbackModal(name,title,description) {
                var serviceNameElem = $('input[name="form_service_name"]');
                if(serviceNameElem){
                    serviceNameElem.val(name);
                }
                var serviceTypeElem = $('input[name="form_service_type"]');
                if(serviceTypeElem){
                    serviceTypeElem.val('event');
                }
                var serviceModalTitleElem = $('#serviceModalTitle');
                if(serviceModalTitleElem){
                    serviceModalTitleElem.text(title);
                }
                var  serviceModalDescriptionElem = $('#serviceModalDescription');
                if(serviceModalDescriptionElem){
                    serviceModalDescriptionElem.text(description);
                }
                $('.modal').modal({
                    dismissible: true
                });
                $('#rateYourExperienceModel').modal('open');
            }

            function closeServiceFeedbackModal(){
                $('#rateYourExperienceModel').modal('close');
                var serviceResultMessage = $("#serviceResultMessage");
                serviceResultMessage.empty();
                setTimeout(function () {
                    $('input:radio[name=form_service_rate_satisfaction]').each(function () {
                        $(this).prop('checked', false);
                        $("#" + $(this).attr("id")).removeClass("active");
                        var parent = $("#" + $(this).attr("id")).parent();
                        if(parent){
                            parent.removeClass("active");
                        }
                    });
                    $("form[name='rateYourExperienceForm']").show();
                }, 3000);

            }
            $(document).ready(function () {
                var ipaddress = "";
                var currentlocation = "";

                //$.getJSON('https://ipapi.co/json/', function(data) {
                //	console.log(JSON.stringify(data, null, 2));
                //});
                var apiKey = '6892e636b45604bab61dae5a54236af92d6969546984e6af12a4a3c9';
                $.getJSON('https://api.ipdata.co?api-key=' + apiKey, function (data) {
                    //var data = JSON.stringify(data, null, 2);
                    if (data) {
                        ipaddress = data.ip;
                        currentlocation = [data.city, '-', data.region, '-', data.country_name].join('');
                    }
                    //console.log("IP " + ipaddress);
                    //console.log("LOCATION " + currentlocation);
                });
                //var hideheader = getUrlVars()["hideheader"];
                var hidefooter = getUrlVars()["hidefooter"];
                var footerElem = $("#footer");
                var sectionElemOne = $("#footersectionone");
                var sectionElemTwo = $("#footersectiontwo");
                if(footerElem != null){
                    if(hidefooter != undefined && hidefooter == "true"){
                        footerElem.addClass("hidden");
                    }
                    else{
                        footerElem.removeClass("hidden");
                    }
                }
                if(sectionElemOne != null){
                    if(hidefooter != undefined && hidefooter == "true"){
                        sectionElemOne.addClass("hidden");
                    }
                    else{
                        sectionElemOne.removeClass("hidden");
                    }
                }
                if(sectionElemTwo != null){
                    if(hidefooter != undefined && hidefooter == "true"){
                        sectionElemTwo.addClass("hidden");
                    }
                    else{
                        sectionElemTwo.removeClass("hidden");
                    }
                }

                let endpoint = 'https://api.nrc.gov.ae';//'http://localhost:54919';
                var langElem = document.getElementsByName("form_lang");
                var langCode = langElem != undefined && langElem.length>0?langElem[0].value:"en";
                // Initialize form validation on the form.
                // It has the name attribute "form_name"
                $("form[name='happiness_form']").validate({
                    // Specify validation rules
                    rules: {
                        // The key name on the left side is the name attribute
                        // of an input field. Validation rules are defined
                        // on the right side
                        form_rate_satisfaction: "required",
                        form_rate_name: "required",
                        form_rate_message:"required",
                        form_rate_email: {
                            required: true,
                            // Specify that email should be validated
                            // by the built-in "email" rule
                            email: true
                        },
                        //password: {
                        //    required: true,
                        //    minlength: 5
                        //}
                    },
                    // Specify validation error messages
                    messages: {
                        form_rate_satisfaction: (langCode=='ar'?"الرجاء اختيار التقييم":"Please select rating"),
                        form_rate_name: (langCode=='ar'?"الرجاء ادخال اسمك":"Please enter your name"),
                        form_rate_message:(langCode=='ar'?"الرجاء ادخال الرسالة الخاصة بك":"Please enter your message"),
                        form_rate_email: (langCode=='ar'?"الرجاء ادخال بريد إلكتروني صالح":"Please enter a valid email address")
                    },
                    submitHandler: function (form) {
                        $('#cover-spin').show(0);
                        event.preventDefault();
                        var form_data = $("form[name='happiness_form']").serializeArray();
                        var ratingId = form_data.find(x => x.name == "form_rate_satisfaction").value,
                            name = form_data.find(x => x.name == "form_rate_name").value,
                            email = form_data.find(x => x.name == "form_rate_email").value,
                            message = form_data.find(x => x.name == "form_rate_message").value,
                            languageId = form_data.find(x => x.name == "form_language_id").value,
                            lang = form_data.find(x => x.name == "form_lang").value,
                            platformTypeId = 103,
                            url = endpoint +'/api/happinessfeedback';

                        var data = {
                            ratingId: parseInt(ratingId,10),
                            name: name,
                            email: email,
                            message: message,
                            languageId: languageId,
                            lang: lang,
                            platformTypeId: platformTypeId
                        };
                        post(url, data).then(response => {
                                var resultMessage = $("#resultMessage");
                                resultMessage.empty();
                                var resp = response;
                                if(resp != null && resp.status == true){
                                    if(resultMessage){
                                        resultMessage.append(lang == "ar"?"تم إرسال ملاحظاتك بنجاح. شكراً جزيلاً!":"Your feedback submitted successfully. Thank you!!!");
                                        changeColor("resultMessage","green");
                                        $("form[name='happiness_form']").trigger("reset");
                                        $("form[name='happiness_form']").hide();
                                    }
                                }
                                $('#cover-spin').hide(0);
                            },
                            error =>{
                                var resultMessage = $("#resultMessage");
                                resultMessage.empty();
                                var err = error;
                                if(err != null && err.responseJSON != null){
                                    if(resultMessage){
                                        resultMessage.append(err.responseJSON.message);
                                        changeColor("resultMessage","red");
                                    }
                                }
                                $('#cover-spin').hide(0);
                            });
                    }
                });
                $("#myModal").on('show.bs.modal', function(){
                    var resultMessage = $("#resultMessage");
                    resultMessage.empty();
                    $("form[name='happiness_form']").show();
                });
                $("#rate_satisfaction_div :input").click(function () {
                    $("#rate_satisfaction_div :input").each(function() {
                        if ($(this).is(":checked")) {
                            $("#" + $(this).attr("id")).addClass("active");
                        } else {
                            $("#" + $(this).attr("id")).removeClass("active");
                        }
                    });
                });

                //Starting of Service feedback script

                $("form[name='rateYourExperienceForm']").validate({
                    rules: {
                        form_service_rate_satisfaction: "required"
                    },
                    // Specify validation error messages
                    messages: {
                        form_service_rate_satisfaction: (langCode == 'ar' ? "الرجاء اختيار التقييم" : "Please select rating")
                    },
                    submitHandler: function (form) {
                        $('#cover-spin').show(0);
                        event.preventDefault();
                        var form_data = $("form[name='rateYourExperienceForm']").serializeArray();
                        var ratingId = form_data.find(x => x.name == "form_service_rate_satisfaction").value,
                            serviceName =form_data.find(x => x.name == "form_service_name").value,
                            serviceType = form_data.find(x=> x.name == "form_service_type").value,
                            message = form_data.find(x => x.name == "form_service_rate_message").value,
                            languageId = form_data.find(x => x.name == "form_service_language_id").value,
                            lang = form_data.find(x => x.name == "form_service_lang").value,
                            platformTypeId = 103,
                            url = endpoint + '/api/servicefeedback';

                        var data = {
                            ratingId: parseInt(ratingId, 10),
                            serviceName: serviceName,
                            serviceType: serviceType,
                            userIPAddress: ipaddress,
                            location: currentlocation,
                            message: message,
                            languageId: languageId,
                            lang: lang,
                            platformTypeId: platformTypeId
                        };
                        post(url, data).then(response => {
                                var serviceResultMessage = $("#serviceResultMessage");
                                serviceResultMessage.empty();
                                var resp = response;
                                if (resp != null && resp.status == true) {
                                    if (serviceResultMessage) {
                                        serviceResultMessage.append(lang == "ar" ? "تم إرسال ملاحظاتك بنجاح. شكراً جزيلاً!" : "Your feedback submitted successfully. Thank you!!!");
                                        changeColor("serviceResultMessage", "green");
                                        $("form[name='rateYourExperienceForm']").trigger("reset");
                                        $("form[name='rateYourExperienceForm']").hide();
                                        setTimeout(function () { closeServiceFeedbackModal(); }, 1000);
                                    }
                                }
                                $('#cover-spin').hide(0);
                            },
                            error => {
                                var serviceResultMessage = $("#serviceResultMessage");
                                serviceResultMessage.empty();
                                var err = error;
                                if (err != null && err.responseJSON != null) {
                                    if (serviceResultMessage) {
                                        serviceResultMessage.append(err.responseJSON.message);
                                        changeColor("serviceResultMessage", "red");
                                    }
                                }
                                $('#cover-spin').hide(0);
                            });
                    }
                });
                $("#rateYourExperienceModel").on('show.bs.modal', function () {
                    var serviceResultMessage = $("#serviceResultMessage");
                    serviceResultMessage.empty();
                    $("form[name='rateYourExperienceForm']").show();
                });
                $("#rate_service_satisfaction_div :input").click(function () {
                    $("#rate_service_satisfaction_div :input").each(function () {
                        if ($(this).is(":checked")) {
                            $("#" + $(this).attr("id")).addClass("active");
                            var parent = $("#" + $(this).attr("id")).parent();
                            if(parent){
                                parent.addClass("active");
                            }
                        } else {
                            $("#" + $(this).attr("id")).removeClass("active");
                            var parent = $("#" + $(this).attr("id")).parent();
                            if(parent){
                                parent.removeClass("active");
                            }
                        }
                    });
                });
            });


        </script>










